mapper.select:
SELECT * FROM messages
WHERE user_id = #{userId} AND user_type = #{userType}
ORDER BY created_at DESC

mapper.update:
UPDATE messages
SET is_read = 1, read_at = #{readAt}
WHERE id = #{id}

UPDATE messages
SET is_read = 1, read_at = #{readAt}
WHERE user_id = #{userId} AND user_type = #{userType} AND is_read = 0
